/**
  * Updates the document title when found.
  *
  * @name RouteTitle
  * @description
 */
export class RouteTitle {
    componentWillLoad() {
        const suffix = this.activeRouter && this.activeRouter.get('titleSuffix') || '';
        document.title = `${this.title}${suffix}`;
    }
    render() {
        return null;
    }
    static get is() { return "stencil-route-title"; }
    static get properties() { return { "activeRouter": { "context": "activeRouter" }, "title": { "type": String, "attr": "title" } }; }
}
